$("#loginBtn").click(function(){var a=$("#uname").val();var b=$("#upwd").val();if((a!="")&&(b!="")){$.ajax({type:"post",url:"data/login.php",data:{uname:a,upwd:b},success:function(c){console.log(c);if(c==-1){alert("用户名或密码错误")}else{alert("登录成功,3秒自动跳转到首页");setTimeout(function(){location.href="index.html"},3000);sessionStorage.uname=a}}})}});